INSERT INTO `rev_pka3` VALUES ('17', 'PKA_21012020083430_1579570470116', '1', 'PKA_21012020083430_1579570470116-3-3', '3-3-A', 'Auditee dapat memahami pentingnya audit yang dilaksanakan, dan dapat membantu kelancaran pelaksaan audit.', '0000-00-00', 'a', '-');
