INSERT INTO `sub_pka2` VALUES ('494', 'PKA_21012020083430_1579570470116', 'PKA_21012020083430_1579570470116-3-12', '3-12-B', '2.	Lakukan pengecekan atas seluruh pengadaan barang yang dibiayai dari Dana Desa dan Dana Bantuan Keuangan Provinsi Jawa Barat Tahun Anggaran 2019;\r\n•	Cek fisik barang dan bandingkan hasil pengadaan barang dengan spesifikasi teknis yang dimuat dalam dokumen perencanaan;\r\n•	Buatkan Berita Acara Hasil Pemeriksaan Fisik Barang nya ditandatangani oleh personal yang melakukan pengecekan/Petugas Pengurus Barang, Ketua Pelaksana Kegiatan dan Tim Audit serta diketahui oleh Kepala Desa.', '2020-01-23', '2.C.1.b', '', null, null, null, null, null, null, null, 'belum');
