INSERT INTO `sub_pka2` VALUES ('483', 'PKA_21012020083430_1579570470116', 'PKA_21012020083430_1579570470116-2-8', '2-8-A', 'Untuk memberikan keyakinan yang memadai bahwa pelaksanaan pekerjaan dan pengadaan yang dibiayai dari Dana Desa dan Dana Bantuan Keuangan Provinsi Jawa Barat Tahun Anggaran 2019 telah dilaksanakan sesuai Gambar Teknik, RAB dan Analisa Harga Satuan Pekerjaan (AHSP) untuk Kegiatan Pekerjaan Konstruksi dan Spesifikasi Teknis untuk pengadaan Barang.', '0000-00-00', 'a', '-', null, null, null, null, null, null, null, 'belum');
