INSERT INTO `sub_pka2` VALUES ('1357', 'PKA_15022020203047_1581773447980', 'PKA_15022020203047_1581773447980-3-8', '3-8-B', 'Lakukan pengecekan lapangan atas seluruh kegiatan pekerjaan konstruksi yang dibiayai dari Dana Desa dan Dana Bantuan Keuangan Provinsi Jawa Barat Tahun Anggaran 2019 Cek/telusuri apakah :\r\n1	Terdapat SK TPK\r\n2	Otorisasi pengajuan SPP\r\n3	Lokasi Kegiatan sesuai yang direncanakan dalam APBDes\r\n4	Pengadaan Material sesuai harga pasar setempat/terdekat\r\n5	Pelaksanaan swakelola\r\n6	Laporan fisik kegiatan\r\n7	RAB, Jadawal Pelaksanaan, Gambar Rencana terpalidasi\r\n8	Volume hasil pekerjaan sesuai rencana\r\n', '2020-02-24', '2.C.2.a', '', null, null, null, null, null, null, null, 'belum');
