INSERT INTO `tb_surat` VALUES ('69', '705/ST- 296 -INSPT/2020', '2020-04-30 00:00:00', 'Dokumen Pelaksanaan Anggaran (DPA) Inspektorat Kabupaten Cianjur Tahun Anggaran 2020 Nomor : 900/Kep.01/BPKAD/2020 Tanggal 2 Januari 2020.', 'TIM_e8bd29f43fbc4b20ca561696d56708ab', '<table border=\"0\" cellpadding=\"0\" cellspacing=\"0\" width=\"0\">\r\n	<tbody>\r\n		<tr>\r\n			<td style=\"width:28px;height:147px;\">\r\n				<p align=\"center\">\r\n					1.</p>\r\n			</td>\r\n			<td style=\"width:519px;height:147px;\">\r\n				<p>\r\n					Melakukan Audit Investigasi atas dugaan Penyalahgunaan/Penyimpangan Dana Desa (DD) Desa Rancagoong Kec. Cilaku Kabupaten Cianjur Tahun Anggaran 2018 s.d 2019 dengan Ruang Lingkup Pemeriksaan terbatas pada pengujian Bukti Pertanggungjawaban Penggunaan Dana Desa dan Bantuan Provinsi Jawa Barat Tahun Anggaran 2019, sedangkan untuk pengujian pekerjaan fisik maupun non fisik cukup dibuktikan dengan surat pernyataan yang di tandatangani oleh Tim Pelaksana Kegiatan (TPK) dan Kepala Desa yang menyatakan bahwa pekerjaan fisik maupun non fisik telah dilaksanakan sesuai RAB dan gambar yang disertai bukti dokumentasi, Kegiatan Pemeriksaan terlampir.</p>\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:28px;height:28px;\">\r\n				<p align=\"center\">\r\n					2.</p>\r\n			</td>\r\n			<td style=\"width:519px;height:28px;\">\r\n				<p>\r\n					Selama Pelaksanaan Audit yang dilakukan di Luar Kantor agar tetap memperhatikan Standar Penanganan Covid-19 (Menggunakan Masker, Jaga Jarak Aman, dan Mencuci tangan/Handsanitizer)</p>\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:28px;\">\r\n				<p>\r\n					3.</p>\r\n			</td>\r\n			<td style=\"width:519px;\">\r\n				<p>\r\n					Menyusun Laporan Hasil Audit (LHA) yang ditandatangani &nbsp;oleh Inspektur Kab. Cianjur.</p>\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n		<tr>\r\n			<td style=\"width:28px;\">\r\n				<p align=\"center\">\r\n					4.</p>\r\n			</td>\r\n			<td style=\"width:519px;\">\r\n				<p>\r\n					Segala Biaya dalam Rangka Pemeriksaan ini, dibebankan&nbsp; pada DPA Inspektorat Kabupaten Cianjur Tahun Anggaran 2020.</p>\r\n				<p>\r\n					&nbsp;</p>\r\n			</td>\r\n		</tr>\r\n	</tbody>\r\n</table>\r\n<p>\r\n	&nbsp;</p>\r\n', '2020-05-04', '2020-05-11', 'input', 'Tim Pemeriksa');
